import React, { useState } from 'react';

function ProductCreatePage() {
  const [productName, setProductName] = useState('');
  const [upc, setUpc] = useState('');
  const [availableOn, setAvailableOn] = useState('');
  const [properties, setProperties] = useState([{ name: '', value: '' }]);

  const handleAddProperty = () => {
    setProperties([...properties, { name: '', value: '' }]);
  };

  const handlePropertyChange = (index, event) => {
    const { name, value } = event.target;
    const updatedProperties = [...properties];
    updatedProperties[index][name] = value;
    setProperties(updatedProperties);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Perform validation
    // Submit data to backend
    console.log('Form submitted:', { productName, upc, availableOn, properties });
  };

  return (
    <div>
      <h2>Product Create Page</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="productName">Product Name:</label>
          <input
            type="text"
            id="productName"
            name="productName"
            value={productName}
            onChange={(e) => setProductName(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="upc">UPC:</label>
          <input
            type="text"
            id="upc"
            name="upc"
            value={upc}
            onChange={(e) => setUpc(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="availableOn">Available On:</label>
          <input
            type="datetime-local"
            id="availableOn"
            name="availableOn"
            value={availableOn}
            onChange={(e) => setAvailableOn(e.target.value)}
            required
          />
        </div>
        <div>
          <h3>Properties:</h3>
          {properties.map((property, index) => (
            <div key={index}>
              <label htmlFor={`propertyName${index}`}>Name:</label>
              <input
                type="text"
                id={`propertyName${index}`}
                name="name"
                value={property.name}
                onChange={(e) => handlePropertyChange(index, e)}
                required
              />
              <label htmlFor={`propertyValue${index}`}>Value:</label>
              <input
                type="text"
                id={`propertyValue${index}`}
                name="value"
                value={property.value}
                onChange={(e) => handlePropertyChange(index, e)}
                required
              />
            </div>
          ))}
          <button type="button" onClick={handleAddProperty}>Add Property</button>
        </div>
        <button type="submit">Save</button>
      </form>
    </div>
  );
}

export default ProductCreatePage;
