  import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ProductCreatePage = () => {
    const [productName, setProductName] = useState('');
    const [upc, setUpc] = useState('');
    const [availableOn, setAvailableOn] = useState('');
    const [properties, setProperties] = useState([{ name: '', value: '' }]);
    const [searchTerm, setSearchTerm] = useState('');
    const [searchResults, setSearchResults] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchSearchResults = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/search?term=${searchTerm}`);
                setSearchResults(response.data.results);
            } catch (error) {
                console.error('Error fetching search results:', error);
            }
        };

        if (searchTerm) {
            fetchSearchResults();
        } else {
            setSearchResults([]);
        }
    }, [searchTerm]);

    const handleAddProperty = () => {
        setProperties([...properties, { name: '', value: '' }]);
    };

    const handlePropertyChange = (index, key, value) => {
        const updatedProperties = [...properties];
        updatedProperties[index][key] = value;
        setProperties(updatedProperties);
    };

    const handleClick = async (e) => {
        e.preventDefault();
        try {
            const formattedProperties = properties.map((property) => {
                return `${property.name}: ${property.value}`;
            });

            const data = {
                productName: productName,
                upc: upc,
                availableOn: availableOn,
                properties: formattedProperties,
            };

            await axios.post('http://localhost:5000/product', data);
            navigate('/');
        } catch (err) {
            console.log(err);
        }
    };

    return (
        <div className="product-create-container">
            <h1>Product Create Page</h1>
            <label>Product Name:</label>
            <input type="text" value={productName} onChange={(e) => setProductName(e.target.value)} />
            <br />
            <label>UPC:</label>
            <input type="text" value={upc} onChange={(e) => setUpc(e.target.value)} />
            <br />
            <label>Available On (yyyy-mm-dd):</label>
            <input type="text" value={availableOn} onChange={(e) => setAvailableOn(e.target.value)} />
            <br />
            <h2>Properties</h2>
            {properties.map((property, index) => (
                <div key={index}>
                    <input
                        type="text"
                        value={property.name}
                        onChange={(e) => handlePropertyChange(index, 'name', e.target.value)}
                        placeholder="Property Name"
                    />
                    <input
                        type="text"
                        value={property.value}
                        onChange={(e) => handlePropertyChange(index, 'value', e.target.value)}
                        placeholder="Property Value"
                    />
                </div>
            ))}
            <button onClick={handleAddProperty}>Add Property</button>
            <br />
            <button onClick={handleClick}>Save</button>
            <br />
            <h2>Search Results</h2>
            <input
                type="text"
                placeholder="Search products..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
            />
            <ul>
                {searchResults.map((result) => (
                    <li key={result.id}>
                        <p>{result.name}</p>
                        <p>{result.description}</p>
                        <p>{result.price}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ProductCreatePage;
